import styles from './RadioArctic.module.scss';
import { useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Button from '../../../components/Button/Button';

function RadioArctic() {
    const navigate = useNavigate();

    const handleClickBack = () => {
        navigate('/arctic');
    };

	 const audioRef = useRef(null);

	const handlePlay = () => {
          
          audioRef.current = new Audio('/path/to/audio.mp3');
          audioRef.current.play().catch(error => {
              console.error('Ошибка воспроизведения:', error);
              // Fallback для браузеров, блокирующих автовоспроизведение
              audioRef.current.muted = true;
              audioRef.current.play();
          });
      };

    return (
        <div className={styles.wrapper}>
            <div className={styles.content}>
                <Button
                    className={styles.button}
                    onClick={handlePlay}
                />

					

                <Button className={styles.button} onClick={handleClickBack} />
            </div>
        </div>
    );
}

export default RadioArctic;
