import styles from './RadioPilot.module.scss';
import { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Button from '../../../components/Button/Button';

function RadioPilot() {
    const navigate = useNavigate();
    const audioRef = (useRef < HTMLAudioElement) | (null > null);
    const [isPlaying, setIsPlaying] = useState(false);

    const handleClickBack = () => {
        // Останавливаем аудио при переходе назад
        if (audioRef.current) {
            audioRef.current.pause();
            audioRef.current.currentTime = 0;
            audioRef.current = null;
        }
        navigate('/pilot');
    };

    const handleTogglePlay = () => {
        // Если аудио уже играет - останавливаем и сбрасываем
        if (isPlaying && audioRef.current) {
            audioRef.current.pause();
            audioRef.current.currentTime = 0;
            setIsPlaying(false);
            return;
        }

        // Создаем новый экземпляр аудио при первом запуске
        if (!audioRef.current) {
            audioRef.current = new Audio('/audio/audio-pilot.mp3');

            // Обработчик окончания воспроизведения
            audioRef.current.addEventListener('ended', () => {
                setIsPlaying(false);
                audioRef.current = null;
            });
        }

        // Запускаем воспроизведение
        audioRef.current
            .play()
            .then(() => {
                setIsPlaying(true);
            })
            .catch(error => {
                console.error('Ошибка воспроизведения:', error);
                // Пробуем воспроизвести с отключенным звуком
                if (audioRef.current) {
                    audioRef.current.muted = true;
                    audioRef.current
                        .play()
                        .then(() => setIsPlaying(true))
                        .catch(e => console.error('Не удалось воспроизвести даже с отключенным звуком:', e));
                }
            });
    };

    return (
        <div className={styles.wrapper}>
            <div className={styles.content}>
                <Button className={styles.button} onClick={handleTogglePlay} />

                <Button className={styles.button} onClick={handleClickBack} />
            </div>
        </div>
    );
}

export default RadioPilot;
